mapper.select:
		SELECT COUNT(STORENO)
          FROM STOREREG
         WHERE STATUS = 0
           AND STOREADDR1 = #{zone}
           AND STOREADDR2 = #{local}
           AND STORECATEGORY = #{catg}
           AND STORENAME = #{query}

SELECT B.*
  FROM (SELECT ROWNUM AS RN, A.*
          FROM (SELECT *
                  FROM STOREREG
                 WHERE STATUS = 0
                   AND STOREADDR1 = #{zone}
		           AND STOREADDR2 = #{local}
		           AND STORECATEGORY = #{catg}
		           AND STORENAME LIKE '%' || #{query} || '%'
                 ORDER BY STORENO DESC) A) B
 WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

SELECT COUNT(NO)
  FROM REVIEW
 WHERE STORENO = #{storeNo}

SELECT AVG(SCORE)
  FROM REVIEW
 WHERE STORENO = #{storeNo}
