mapper.select:
SELECT COUNT(MEMBERID)
  FROM MEMBER
 WHERE MEMBERID = #{id}

SELECT COUNT(MEMBEREMAIL)
  FROM MEMBER
 WHERE MEMBEREMAIL = #{email}

SELECT MEMBERID, MEMBERNAME
  FROM MEMBER
 WHERE MEMBEREMAIL = #{email}

SELECT MEMBERID, MEMBERNAME,MEMBERPW
  FROM MEMBER
 WHERE MEMBEREMAIL = #{email}
