mapper.update:
UPDATE STOREREG
   SET STOREHIT = storeHit + 1
 WHERE STORENO = #{storeNo}

mapper.select:
SELECT B.* FROM (SELECT ROWNUM AS RN, A.* FROM (SELECT STORENO, STORENAME, STORECONTENT, STORETABLE, STORETEL, STOREADDR1, STOREADDR2, STOREADDR3, STORETIME, STORESNS, STORECATEGORY, STOREMENU, ORIGINFILENAME, SAVEFILENAME, STOREHIT FROM STOREREG
ORDER BY STOREHIT ASC
ORDER BY STORENO DESC ) A) B WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

mapper.update:
UPDATE STOREREG
   SET STORENAME = #{storeName}, STORECONTENT = #{storeContent}, STORETABLE = #{storeTable}, STORETEL = #{storeTel}, STOREADDR1 = #{storeAddr1},
       STOREADDR2 = #{storeAddr2}, STOREADDR3 = #{storeAddr3},
       STORETIME = #{storeTime}, STORESNS = #{storeSns}, STORECATEGORY = #{storeCategory},
       STOREMENU = #{storeMenu}, ORIGINFILENAME = #{originFilename}, SAVEFILENAME = #{saveFilename}
 WHERE STORENO = #{storeNo}

mapper.delete:
UPDATE STOREREG
   SET STATUS = -1
 WHERE STORENO = #{storeNo}
